# On ajoute (liste) pour que la méthode accepte une information
def count(liste)
  # À l'intérieur, on travaille avec 'liste'
  nombre_total = liste.length
  puts "Il y a #{nombre_total} handles"
end

def filtrer(trieur)
  # 1. On trouve la taille minimum
  taille_min = trieur.min_by { |x| x.length }.length

  # 2. On crée la liste de tous ceux qui ont cette taille
  gagnants = trieur.select { |x| x.length == taille_min }

  # 3. On choisit le message selon le nombre de gagnants
  if gagnants.length == 1
    puts "Voici le plus petit : #{gagnants[0]}"
  else
    puts "Voici les plus petits ( ils ont le meme nombre de lettre ) : #{gagnants}"
  end
end

def compter_cinq(trieur)
  # On parcourt la liste et on compte seulement si la taille est 6
  resultat = trieur.count { |x| x.length == 6 }
  
  puts "Il y a #{resultat} handles avec exactement 5 caractères."
end

def compter_majuscules(trieur)
  # On compte si le caractère à l'index 1 est égal à sa version MAJUSCULE
  resultat = trieur.count { |x| x[1] == x[1].upcase }
  
  puts "Il y a #{resultat} handles qui commencent par une majuscule."
end

def tri_alphabetique(trieur)
  resultat = trieur.sort
  puts "handle dans l'ordre alphabétique : #{resultat}"
end

def trouver_position(trieur)
  position = trieur.index("@epenser")
  if position
    puts "@epenser est à la position : #{position}"
  else
    puts "@epenser n'est pas dans la liste."
  end
end

def repartition_exhaustive(trieur)
  # Trouver la taille la plus grande pour définir la limite de la boucle
  max_t = trieur.max_by { |x| x.length }.length

  # Boucler de 1 jusqu'à la taille maximale trouvée
  (1..max_t).each do |taille|
    nombre = trieur.count { |x| x.length == taille }
    puts "Taille #{taille} : #{nombre} handle(s)"
  end
end

def effectuer_toutes_les_taches(trieur)
  puts "--- DÉBUT DE L'ANALYSE ---"
  count(trieur)
  filtrer(trieur)
  compter_cinq(trieur)
  compter_majuscules(trieur)
  tri_alphabetique(trieur)
  trouver_position(trieur)
  repartition_exhaustive(trieur)
  puts "--- FIN DE L'ANALYSE ---"
end

# Exécution final
effectuer_toutes_les_taches(handle)

#count(handle) compte tout les handle 
#filtrer(handle) affiche le/les plus petit handle
